SELECT TIPDATA.YEAR YEAR, TIPDATA.DBNUM DBNUM, TIPDATA.REVISION REVISION, TIPDATA.DBNUM2 DBNUM2, TIPDATA.DBNUM3 DBNUM3, TIPDATA.PROJ_NAME PROJ_NAME, TIPDATA.LEAD_AGENCY LEAD_AGENCY, TIPDATA.PROJ_CONTACT PROJ_CONTACT, TIPDATA.PROJ_PHNUM PROJ_PHNUM, TIPDATA.PROJECT_TYPE PROJECT_TYPE, TIPDATA.MODE MODE, TIPDATA.REMARKS REMARKS, TIPDATA.CORRIDOR CORRIDOR, TIPDATA.ROUTE ROUTE, TIPDATA.ROUTE_2 ROUTE_2, TIPDATA.MAP_REFERENCE MAP_REFERENCE, TIPDATA.MAP_LAYER_ID MAP_LAYER_ID, TIPDATA.LIMITS LIMITS, TIPDATA.DESCRIPT DESCRIPT, TIPDATA.OBJECTIVE OBJECTIVE, TIPDATA.MODECAT MODECAT, TIPDATA.HWYCAT HWYCAT, TIPDATA.FUNCCAT FUNCCAT, TIPDATA.FHWACAT FHWACAT, TIPDATA.PROJCAT PROJCAT, TIPDATA.MPO_CAT MPO_CAT, TIPDATA.TRANS TRANS, TIPDATA.AIR_QUALITY AIR_QUALITY, TIPDATA.EIS EIS, TIPDATA.LEGDIST1 LEGDIST1, TIPDATA.LEGDIST2 LEGDIST2, TIPDATA.LEGDIST3 LEGDIST3, TIPDATA.LEGDIST4 LEGDIST4, TIPDATA.STATE STATE, TIPDATA.COUNTY1 COUNTY1, TIPDATA.COUNTY2 COUNTY2, TIPDATA.COUNTY3 COUNTY3, TIPDATA.COUNTY4 COUNTY4, TIPDATA.COUNTYPERC1 COUNTYPERC1, TIPDATA.COUNTYPERC2 COUNTYPERC2, TIPDATA.COUNTYPERC3 COUNTYPERC3, TIPDATA.COUNTYPERC4 COUNTYPERC4, TIPDATA.MUNC1 MUNC1, TIPDATA.MUNC2 MUNC2, TIPDATA.MUNC3 MUNC3, TIPDATA.MUNC4 MUNC4, TIPDATA.M_POSTS_FROM M_POSTS_FROM, TIPDATA.M_POSTS_TO M_POSTS_TO, TIPDATA.LENGTH LENGTH, TIPDATA.SPONSOR SPONSOR, TIPDATA.OVERWEIGHT OVERWEIGHT, TIPDATA.LARGE_TRUCK_VOLUME LARGE_TRUCK_VOLUME, TIPDATA.SEVERITY SEVERITY, TIPDATA.BLUE_ROUTE BLUE_ROUTE, TIPDATA.INTERMODAL INTERMODAL, TIPDATA.NJ_ACCESS NJ_ACCESS, TIPDATA.NHS NHS, TIPDATA.BTI BTI, TIPDATA.LARGE_TRUCK_CRASH_RATE LARGE_TRUCK_CRASH_RATE
FROM tip_data TIPDATA
WHERE 1=1 AND YEAR = ${YEAR} AND DBNUM = ${DBNUM} AND REVISION = ${REVISION}

SELECT TIPFIN.YEAR YEAR, TIPFIN.DBNUM DBNUM, TIPFIN.REVISION REVISION, TIPFIN.PHASE PHASE, TIPFIN.WORK WORK, TIPFIN.SOURCE SOURCE, TIPFIN.FUNDING_TYPE FUNDING_TYPE, TIPFIN.AMOUNT AMOUNT, TIPFIN.ACTUAL_FUNDING ACTUAL_FUNDING, TIPFIN.PLAN_BEGIN_YEAR PLAN_BEGIN_YEAR, TIPFIN.PLAN_BEGIN_QTR PLAN_BEGIN_QTR, TIPFIN.PLAN_END_YEAR PLAN_END_YEAR, TIPFIN.PLAN_END_QTR PLAN_END_QTR, TIPFIN.ACT_BEGIN_YEAR ACT_BEGIN_YEAR, TIPFIN.ACT_BEGIN_QTR ACT_BEGIN_QTR, TIPFIN.ACT_END_YEAR ACT_END_YEAR, TIPFIN.ACT_END_QTR ACT_END_QTR FROM tip_financial TIPFIN
WHERE YEAR = ${YEAR} AND DBNUM = ${DBNUM} AND REVISION = ${REVISION}
ORDER BY TIPFIN.PLAN_BEGIN_YEAR

SELECT     TIPDATA.REVISION REVISION

FROM        tip_data TIPDATA

WHERE       YEAR = ${YEAR} AND DBNUM = ${DBNUM}

		                                SELECT   	TIPDATA.PROJ_NAME + '||' +  TIPDATA.PROGMANAG AS PROJECTCASCADE
										                                FROM     	tip_data TIPDATA
                                WHERE       	${AGENCY}
